sys_script_include.script:
var IsTechnicianAssigned = Class.create();
IsTechnicianAssigned.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    type: 'IsTechnicianAssigned',
	getFields: function() {
		var sysId = this.getParameter('sysparm_incident_id');
		var userId = this.getParameter('sysparm_user_id');
		var gr = new GlideRecord('incident');
		gr.addQuery('sys_id', sysId);
		gr.query();
		if (gr.hasNext()) {
			while (gr.next()) {
				var assigned = gr.getValue('assigned_to');
				return assigned;
			}
		}
		return false;
	}
});
